const initialState = {
    data: [],
    loading: true
}

export const setProducts = (productsData) => ({type: 'SET_PRODUCTS', data: productsData})
export const setLoading = () => ({type: 'SET_LOADING'})

export const productsReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_PRODUCTS':
            return {
                data: action.data,
                loading: false
            }
        case 'SET_LOADING':
            return {
                data: [],
                loading: !state.loading
            }
        default:
            return state
    }
}
