const initialState = {
    data: [],
    loading: true
}

export const setProducts = (productsData) => ({type: 'SET_PRODUCTS', data: productsData})

export const productsReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_PRODUCTS':
            return {
                data: action.data,
                loading: false
            }
        case 'SET_LOADING':
            return !state.loading
        default:
            return state
    }
}
