import React, { useEffect, useState } from 'react'
import './products.scss'
import { connect } from 'react-redux'
import { setProducts, setLoading } from '../../redux/reducers/products-reducer'
import axios from 'axios'
import Spinner from '../../UI/spinner/spinner'


const ProductsComponent = props => {

    const [activePage, setPage] = useState(1)
    const [dataProducts, setDataProducts] = useState([])

    useEffect(() => {
        axios.get('https://store-16405.firebaseio.com/js/-M57zgVEMyvOop9Bq0JT.json')
                .then(res => {
                    setProducts(res.data)
                    setDataProducts(res.data.splice(0, 12))
                    setPage(1)
                })
    }, [])




    const setActivePage = (e) => {
        switch (e.target.id) {
            case "1": {
                const dataProducts = this.props.products.slice(0, 12)
                    setDataProducts(dataProducts)
                    setPage(1)
            }
                return this.state
            case "2": {
                const dataProducts = this.props.products.slice(12, 24)
                    setDataProducts(dataProducts)
                    setPage(2)
            }
                return this.state
            case "3": {
                const dataProducts = this.props.products.slice(24, 36)
                    setDataProducts(dataProducts)
                    setPage(3)
            }
                return this.state
            case "4": {
                const dataProducts = this.props.products.slice(36, 48)
                    setDataProducts(dataProducts)
                    setPage(4)
            }
                return this.state
            case "5": {
                const dataProducts = this.props.products.slice(48, 60)
                    setDataProducts(dataProducts)
                    setPage(5)
            }
                return this.state
            case "6": {
                const dataProducts = this.props.products.slice(60, 72)
                    setDataProducts(dataProducts)
                    setPage(6)
            }
                return this.state
            case "7": {
                const dataProducts = this.props.products.slice(72, 84)
                    setDataProducts(dataProducts)
                    setPage(7)
            }
                return this.state
            case "8": {
                const dataProducts = this.props.products.slice(84, 96)
                    setDataProducts(dataProducts)
                    setPage(8)
            }
                return this.state
            case "9": {
                const dataProducts = this.props.products.slice(96, 108)
                    setDataProducts(dataProducts)
                    setPage(9)
            }
                return this.state
            default:
                return (
                    setDataProducts([])
                )
        }
    }

        return (
            <div>
                {
                    props.loading
                    ? <Spinner />
                    : 
                    <div className="productsPage">
                <div className="productsPagination">
                    {
                        dataProducts.map(i => {
                            const num = this.props.products.indexOf(i)

                            if (num % 12 === 0) {
                                return (
                                    <div id={num / 12 + 1}
                                        key={num / 12 + 230}
                                        className={activePage === num / 12 + 1
                                            ? "btnPagination active"
                                            : "btnPagination"}
                                        onClick={setActivePage}>
                                        {num / 12 + 1}
                                    </div>
                                )
                            }
                        }
                        )
                    }
                </div>

                <div className="productsBox">

                    {
                        dataProducts.map(product => {
                            return (
                                <div key={product.isbn13} className="productsPage">
                                    <div className="productsPagination">

                                    </div>
                                    <div className="productsBox">
                                        <img src={product.image} alt="sorry bro :'(" />
                                        <h3> {product.title} </h3>
                                        <p className="productPrice" > {product.price} </p>
                                        <div className="boxButton" >
                                            <button id={product.isbn13} className="productButton">Add To Basket</button>
                                        </div>
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
                }
            </div>

        )
}

function mapStateToProps(state) {
    return {
        products: state.products.data,
        loading: state.products.loading
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setProducts: (productsData) => dispatch(setProducts(productsData)),
        setLoading: () => dispatch(setLoading)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ProductsComponent)
