              "url": "https://itbook.store/books/9781617293344"
            },
            {
              "title": "Reactive Web Applications",
              "subtitle": "Covers Play, Akka, and Reactive Streams",
              "isbn13": "9781633430099",
              "price": "$35.99",
              "image": "https://itbook.store/img/books/9781633430099.png",
              "url": "https://itbook.store/books/9781633430099"
            },
            {
              "title": "React.js Essentials",
              "subtitle": "A fast-paced guide to designing and building scalable and maintainable web apps with React.js",
              "isbn13": "9781783551620",
              "price": "$23.99",
              "image": "https://itbook.store/img/books/9781783551620.png",
              "url": "https://itbook.store/books/9781783551620"
            }
          ]
        axios.post('https://store-16405.firebaseio.com/react.json', data)
        .then(res => console.log(res))
    }

    render() {
        return (
            <div>
                {
                    this.props.products.map(product => {
                        return (
                            <div>
                                <img src={product.image} />
                                <h3> {product.title} </h3>
                                <p> {product.price} </p>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        products: state.products
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setProducts: (productsData) => dispatch(setProducts(productsData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Products)
