import React, { Component } from 'react'
import Catalog from '../catalog/catalog'
import Products from '../products/products'
import Header from '../header/header'
import {Route} from 'react-router-dom'
import Basket from '../basket/basket'



class App extends Component{
  state = {
    classListData: ["background"]
  }

  setBackgroundColor = () => {
    const newClassList = ["background", "active"]
    this.setState({  
      classListData: newClassList
    })
  }

  unSetBackgroundColor = () => {
    const newClassList = ["background"]
    this.setState({  
      classListData: this.state.classListData.splice(1)
    })
  }

      render() {

        const CatalogComponent = () =>
          <Catalog
            setBackgroundColor={this.setBackgroundColor}
            unSetBackgroundColor={this.unSetBackgroundColor} />

        return (
            <div className={this.state.classListData.join(' ')}>
              <div className="container">
                <Header />
                <Route path="/basket" component={Basket} />
                <Route path="/" exact component={CatalogComponent} />
                <Route path="/" exact component={Products} />
              </div>
            </div>
        )
      }

}

export default App;
