import React, { Component } from 'react'
import './products.scss'
import { connect } from 'react-redux'
import { setProducts, setLoading } from '../../redux/reducers/products-reducer'
import axios from 'axios'
import Spinner from '../../UI/spinner/spinner'


class Products extends Component {

    state = {
        activePage: 1,
        data: []
    }

    componentDidMount() {
        if (this.state.data.length == false) {
            axios.get('https://store-16405.firebaseio.com/js/-M57zgVEMyvOop9Bq0JT.json')
                .then(res => {
                    this.props.setProducts(res.data)
                    this.setState({
                        data: res.data.splice(0, 12)
                    })
                })
        } else {
            this.setState({
                data: this.props.products.splice(0, 12)
            })
        }
    }

    componentDidUpdate() {
        console.log('update')
    }

    setActivePage = (e) => {
        switch (e.target.id) {
            case "1": {
                const dataProducts = this.props.products.slice(0, 12)
                this.setState({
                    activePage: 1,
                    data: dataProducts
                })
            }
                return this.state
            case "2": {
                const dataProducts = this.props.products.slice(12, 24)
                this.setState({
                    activePage: 2,
                    data: dataProducts
                })
            }
                return this.state
            case "3": {
                const dataProducts = this.props.products.slice(24, 36)
                this.setState({
                    activePage: 3,
                    data: dataProducts
                })
            }
                return this.state
            case "4": {
                const dataProducts = this.props.products.slice(36, 48)
                this.setState({
                    activePage: 4,
                    data: dataProducts
                })
            }
                return this.state
            case "5": {
                const dataProducts = this.props.products.slice(48, 60)
                this.setState({
                    activePage: 5,
                    data: dataProducts
                })
            }
                return this.state
            case "6": {
                const dataProducts = this.props.products.slice(60, 72)
                this.setState({
                    activePage: 6,
                    data: dataProducts
                })
            }
                return this.state
            case "7": {
                const dataProducts = this.props.products.slice(72, 84)
                this.setState({
                    activePage: 7,
                    data: dataProducts
                })
            }
                return this.state
            case "8": {
                const dataProducts = this.props.products.slice(84, 96)
                this.setState({
                    activePage: 8,
                    data: dataProducts
                })
            }
                return this.state
            case "9": {
                const dataProducts = this.props.products.slice(96, 108)
                this.setState({
                    activePage: 9,
                    data: dataProducts
                })
            }
                return this.state
            default:
                return (
                    this.setState({
                        data: [],
                        activePage: 1
                    })
                )
        }
    }

    render() {
        return (
            <div>
                {
                    this.props.loading
                    ? <Spinner />
                    : 
                    <div className="productsPage">
                <div className="productsPagination">
                    {
                        this.props.products.map(i => {
                            const num = this.props.products.indexOf(i)

                            if (num % 12 === 0) {
                                return (
                                    <div id={num / 12 + 1}
                                        key={num / 12 + 230}
                                        className={this.state.activePage === num / 12 + 1
                                            ? "btnPagination active"
                                            : "btnPagination"}
                                        onClick={this.setActivePage}>
                                        {num / 12 + 1}
                                    </div>
                                )
                            }
                        }
                        )
                    }
                </div>

                <div className="productsBox">

                    {
                        this.state.data.map(product => {
                            return (
                                <div key={product.isbn13} className="productsPage">
                                    <div className="productsPagination">

                                    </div>
                                    <div className="productsBox">
                                        <img src={product.image} alt="sorry bro :'(" />
                                        <h3> {product.title} </h3>
                                        <p className="productPrice" > {product.price} </p>
                                        <div className="boxButton" >
                                            <button id={product.isbn13} className="productButton">Add To Basket</button>
                                        </div>
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
                }
            </div>

        )
    }
}

function mapStateToProps(state) {
    return {
        products: state.products.data,
        loading: state.products.loading
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setProducts: (productsData) => dispatch(setProducts(productsData)),
        setLoading: () => dispatch(setLoading)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Products)
