import React, { Component } from 'react'
import './catalog.scss'
import { connect } from 'react-redux'
import axios from 'axios'
import { setCatalog } from '../../redux/reducers/catalog-reducer'
import { setProducts } from '../../redux/reducers/products-reducer'
import { data } from '../../data'

class Catalog extends Component {

    componentDidMount() {
        // axios.get('https://store-16405.firebaseio.com/catalog/-M5Ah4OZVoDU6KWiiFqs.json')
        // .then(res => this.props.setCatalog(res.data))
        axios.post('https://store-16405.firebaseio.com/catalog.json', data)
    }

render(){
    return (
        <ul className="catalog">
            {
                this.props.catalog.map(el => {
                    return (
                        <li key={el.id} className="catalogItemList">
                            <div className="catalogItem">
                                <img
                                    src={el.imgURL} alt="sorry :'(" />
                                <h3 className="itemName">{el.title}</h3>
                            </div>
                                { el.subCatalog 
                                ? el.subCatalog.map(i => {
                                    return (
                                        <ul key={i.id} className="subCatalog" >
                                            <li  id={i.id} className="subCatalogItem"> 
                                                <img src={i.imgURL} className="subIcon"/>
                                                <div><strong> {i.title} </strong></div>
                                            </li>
                                         </ul>
                                    )
                                })
                                : null }
                        </li>
                    )
                })
            }
        </ul>
    )
}
}

function mapStateToProps(state) {
    return {
        catalog: state.catalog
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setCatalog: (catalogData) => dispatch(setCatalog(catalogData)),
        setProducts: (productsData) => dispatch(setProducts(productsData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Catalog)
