import React, { Component } from 'react'
import Catalog from '../catalog/catalog'
import Products from '../products/products'
import Header from '../header/header'
import {Route} from 'react-router-dom'
import Basket from '../basket/basket'



class App extends Component{
  state = {
    classListData: ["backgroundColor"]
  }

  setBackgroundColor = () => {
    const newClassList = ["backgroundColor", "active"]
    this.setState({  
      classListData: newClassList
    })
  }


  unSetBackgroundColor = () => {

    const newClasses = this.state.classListData
    newClasses[1] = 'middleWare'
    console.log(newClasses)

    // this.setState({
    //   classListData: classes
    // })
    setTimeout(() => {
      const newClassList = ["backgroundColor"]
      this.setState({  
        classListData: newClassList
    })
    }, 1000)
  }



      render() {

        const CatalogComponent = () =>
          <Catalog
            setBackgroundColor={this.setBackgroundColor}
            unSetBackgroundColor={this.unSetBackgroundColor} />

        return (
            <div className={this.state.classListData.join(' ')}>
              <div className="container">
                <Header />
                <Route path="/basket" component={Basket} />
                <Route path="/" exact component={CatalogComponent} />
                <Route path="/" exact component={Products} />
              </div>
            </div>
        )
      }

}

export default App;
