import React, {Component} from 'react'
import './products.scss'
import { connect } from 'react-redux'
import { setProducts } from '../../redux/reducers/products-reducer'
import axios from 'axios'
import {data} from '../../data'


class Products extends Component {

    componentDidMount() {
        // axios.post('https://store-16405.firebaseio.com/react.json', data)
        // .then(res => console.log(res))
        axios.get('https://store-16405.firebaseio.com/react/-M57sq-jr2tCXdiYLaia.json')
        .then(res => console.log(res.data.slice(0, 12)))
    }

    render() {
        return (
            <div>
                {
                    this.props.products.map(product => {
                        return (
                            <div>
                                <img src={product.image} />
                                <h3> {product.title} </h3>
                                <p> {product.price} </p>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        products: state.products
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setProducts: (productsData) => dispatch(setProducts(productsData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Products)
