import React from 'react'
import './catalog.scss'
import { connect } from 'react-redux'

const Catalog = props => {

    return (
        <div className="catalog">
            {
                props.catalog.map(el => {
                    return (
                        <div key={el.id} className="catalogItem">
                            <img
                              onMouseEnter={props.setBackgroundColor}
                              onMouseLeave={props.unSetBackgroundColor}
                              src={el.imgURL} alt="sorry :'(" />
                            <h3>{el.title}</h3>
                        </div>
                    )
                })
            }
        </div>
    )
}

function mapStateToProps(state) {
    return {
        catalog: state.catalog
    }
}

export default connect(mapStateToProps)(Catalog)
