import React, { Component } from 'react'
import './catalog.scss'
import { connect } from 'react-redux'
import axios from 'axios'
import { setCatalog } from '../../redux/reducers/catalog-reducer'

class Catalog extends Component {

    componentDidMount() {
        axios.get('https://store-16405.firebaseio.com/catalog/-M58ujG91YJv1f_9GEXI.json')
        .then(res => this.props.setCatalog(res.data))
    }

render(){
    return (
        <div className="catalog">
            {
                this.props.catalog.map(el => {
                    return (
                        <div key={el.id} className="catalogItem">
                            <img
                                src={el.imgURL} alt="sorry :'(" />
                            <h3 className="itemName">{el.title}</h3>
                        </div>
                    )
                })
            }
        </div>
    )
}
}

function mapStateToProps(state) {
    return {
        catalog: state.catalog
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setCatalog: (catalogData) => dispatch(setCatalog(catalogData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Catalog)
