import React, {Component} from 'react'
import './products.scss'
import { connect } from 'react-redux'
import { setProducts } from '../../redux/reducers/products-reducer'
import axios from 'axios'


class Products extends Component {

    state = {
        activePage: 1
    }

    componentDidMount() {
        axios.get('https://store-16405.firebaseio.com/js/-M57zgVEMyvOop9Bq0JT.json')
        .then(res => this.props.setProducts(res.data))
    }


    // DO A METHOD FOR PAGINATION. GET ITEMS FROM REDUX AND SPLICE EM
    splicePagination = () => {

    }

    render() {
        return (
        <div className="productsPage">
            <div className="productsPagination">
                {
                    this.props.products.map(i => {
                        const num = this.props.products.indexOf(i)

                        if (num % 12 == 0) {
                            return (
                                <div  id={num / 12 + 1}
                                 className={this.state.activePage == num / 12 + 1 
                                    ? "btnPagination active"
                                    : "btnPagination"}>
                                    {num / 12 + 1}
                                </div>
                            )
                        }
                    }
                    )
                }
            </div>

            <div className="productsBox">

                {
                    this.props.products.map(product => {
                        return (
                            <div className="productsPage">
                                <div className="productsPagination">

                                </div>
                                <div key={product.isbn13} className="productsBox">
                                    <img src={product.image} />
                                    <h3> {product.title} </h3>
                                    <p className="productPrice" > {product.price} </p>
                                    <div className="boxButton" >
                                        <button id={product.isbn13} className="productButton">Add To Basket</button>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        products: state.products
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setProducts: (productsData) => dispatch(setProducts(productsData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Products)
