import React, {Component} from 'react'
import './products.scss'
import { connect } from 'react-redux'
import { setProducts } from '../../redux/reducers/products-reducer'
import axios from 'axios'


class Products extends Component {

    state = {
        activePage: 1,
        data: []
    }

    componentDidMount() {
        axios.get('https://store-16405.firebaseio.com/js/-M57zgVEMyvOop9Bq0JT.json')
        .then(res => {
            this.props.setProducts(res.data)
            this.setState({
                data: res.data.splice(0, 12)
            })
        })
    }


    // DO A METHOD FOR PAGINATION. GET ITEMS FROM REDUX AND SPLICE EM
    splicePagination = () => {

    }

    setActivePage = (e) => {
        this.setState({
            activePage: e.target.id
        })
        switch (e.target.id) {
            case 1: {
                const dataProducts = this.props.products.splice(0, 12)
                this.setState({
                    data: dataProducts
                })
            }
            case 2: {
                const dataProducts = this.props.products.splice(12, 24)
                this.setState({
                    data: dataProducts
                })
                console.log(dataProducts)
            }
        }
    }

    render() {
        return (
        <div className="productsPage">
            <div className="productsPagination">
                {
                    this.props.products.map(i => {
                        const num = this.props.products.indexOf(i)

                        if (num % 12 == 0) {
                            return (
                                <div    id={num / 12 + 1}
                                        key={num / 12 + 230}
                                        className={this.state.activePage == num / 12 + 1 
                                        ? "btnPagination active"
                                        : "btnPagination"}
                                        onClick={this.setActivePage}>
                                        {num / 12 + 1}
                                </div>
                            )
                        }
                    }
                    )
                }
            </div>

            <div className="productsBox">

                {
                    this.state.data.map(product => {
                        return (
                            <div key={product.isbn13} className="productsPage">
                                <div className="productsPagination">

                                </div>
                                    <div className="productsBox">
                                    <img src={product.image} />
                                    <h3> {product.title} </h3>
                                    <p className="productPrice" > {product.price} </p>
                                    <div className="boxButton" >
                                        <button id={product.isbn13} className="productButton">Add To Basket</button>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        products: state.products
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setProducts: (productsData) => dispatch(setProducts(productsData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Products)
